(854, 'Civil War', 'http://books.google.com/books/content?id=Z1CgNwAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', NULL, NULL, 'Comics & Graphic Novels', NULL, '0785121781', NULL, 'Marvel', '2008-11-26', NULL, 'Contains materials originally published in magazine form as Civil War #1-7, Marvel Spotlight: Civil war, and Civil War script book.', 512),
(855, 'Captain America Epic Collection: Blood and Glory', 'http://books.google.com/books/content?id=-IDIxwEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', NULL, NULL, 'Comics & Graphic Novels', NULL, '1302922793', NULL, 'Marvel', '2020-03-31', NULL, 'Cap like you\'ve never seen him before! First, it\'s Captain America\'s landmark 400th issue - and while Operation: Galactic Storm rages around him, Steve Rogers must face a gauntlet of his most formidable foes! But will the cosmic conflict\'s fallout sunder the Avengers? Then, prepare for one of the wildest stories the Sentinel of Liberty has ever seen, as Steve is transformed into a howling mad Cap-Wolf! Other werewolves including Man-Wolf, Werewolf by Night and the New Mutants\' Wolfsbane join the fur-flying fun - as do Wolverine and Cable! But who will be the leader of the pack? Plus, when Diamondback is captured by Crossbones, it\'s Cap and Falcon to the rescue! But where\'s D-Man? And Steve Rogers joins Frank Castle in a brutal battle for blood and glory! COLLECTING: VOL. 18: CAPTAIN AMERICA (1968) 398-410, PUNISHER/CAPTAIN AMERICA:BLOOD & GLORY (1992) 1-3', 496),
(856, 'Spider-Man/Deadpool Vol. 9', 'http://books.google.com/books/content?id=hJPcvgEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', 'Marvel Comics', NULL, 'Comics & Graphic Novels', NULL, '1302914634', NULL, 'Marvel', '2019-07-23', NULL, 'The \"INFINITE HOUSE OF CIVIL yet SECRET CRISIS WAR INVASIONS\" begins here! The BIGGEST arc of SPIDER-MAN/DEADPOOL will shake the foundations of the Marvel Universe! Nothing will ever be the same after this! NOTHING! Spider-Man and Deadpool just BARELY make it back from the Negative Zone in the middle of an Event that the Marvel U\'s heroes have already lost. COLLECTING: SPIDER-MAN/DEADPOOL 46-50', 112),
(857, 'Contagion', 'http://books.google.com/books/content?id=DodDxwEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', NULL, NULL, 'Comics & Graphic Novels', NULL, '1302921894', NULL, 'Marvel', '2020-01-28', NULL, 'A NEW EVIL BEYOND UNDERSTANDING COMES TO THE MARVEL UNIVERSE! A seemingly unstoppable force has invaded the Marvel Universe, and it\'s going to take every hero the streets of New York has to stop it! A strange substance is taking over heroes and citizens alike, draining them of their life force, stealing their knowledge and abilities, and neither science nor magic can stop it! Where did it come from? Can anything destroy it? And who will fall to its awful, overwhelming power? COLLECTING: CONTAGION 1-5', 112);
